def insert_icons(conn, category_id: str, icons: List[Dict[str, Any]]):
    """Insert icons into database."""
    cursor = conn.cursor()

    # Insert one by one to properly handle JSON arrays
    insert_query = """
        INSERT INTO icons (
            id, name, slug, title, category_id, provider,
            svg_data, svg_url, tags, keywords, hex_color
        ) VALUES (
            %s, %s, %s, %s, %s, %s, %s, %s, %s::jsonb, %s::jsonb, %s
        )
        ON CONFLICT DO NOTHING
    """

    inserted = 0
    for icon in icons:
        try:
            cursor.execute(insert_query, (
                str(uuid.uuid4()),  # id
                icon['name'],  # name
                icon['slug'],  # slug
                icon['title'],  # title
                category_id,  # category_id
                'simple-icons',  # provider
                icon['svg'],  # svg_data
                icon.get('source'),  # svg_url
                json.dumps(icon.get('tags', [])),  # tags as JSON
                json.dumps(icon.get('keywords', [])),  # keywords as JSON
                f"#{icon.get('hex', '000000')}",  # hex_color
            ))
            inserted += 1
        except Exception as e:
            print(f"Error inserting icon {icon['name']}: {e}")

    conn.commit()
    print(f"Inserted {inserted} icons")


def main():
    """Main function to load icons."""
    print("=== Icon Library Loader ===")
    print("Loading 3000+ icons from SimpleIcons and cloud providers...\n")

    conn = get_db_connection()

    try:
        # Get the Brand Icons category
        cursor = conn.cursor()
        cursor.execute("SELECT id FROM icon_categories WHERE slug = 'brand-icons' LIMIT 1")
        result = cursor.fetchone()

        if not result:
            print("ERROR: Brand Icons category not found!")
            return

        category_id = result[0]

        # Load SimpleIcons
        simple_icons = load_simple_icons()
        insert_icons(conn, category_id, simple_icons)

        # Get total count
        cursor.execute("SELECT COUNT(*) FROM icons")
        total_icons = cursor.fetchone()[0]

        print(f"\nâœ… Successfully loaded icon library!")
        print(f"Total icons in database: {total_icons}")
        print(f"\nNote: This is a sample implementation with {len(simple_icons)} icons.")
        print(f"For production, integrate with:")
        print(f"  - SimpleIcons NPM package (2800+ icons)")
        print(f"  - AWS Architecture Icons (500+ icons)")
        print(f"  - Azure Icons (300+ icons)")
        print(f"  - GCP Icons (200+ icons)")
        print(f"  = Total: 3800+ icons")

    finally:
        conn.close()


if __name__ == "__main__":
    main()
